    private void btnGuardarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnGuardarActionPerformed
  
        Servicio nuevoServicio = new Servicio();
        
        nuevoServicio.setNombre(txtNombreServicio.getText());
        nuevoServicio.setCosto( Integer.valueOf(txtCostoMensual.getText()));
        nuevoServicio.setFechaRegistro(DateTimeFormatter.ofPattern("dd/mm/yyyy")
                    .format(LocalDateTime.now()));
        int tipo = cboCategoria.getSelectedIndex();

            switch (tipo) {
                case 1 -> nuevoServicio.setCategoria(Categoria.Entretencion);
                case 2 -> nuevoServicio.setCategoria(Categoria.Aplicacion);
                case 3 -> nuevoServicio.setCategoria(Categoria.Servicio);
            }

        if(validarExistencia(nuevoServicio) == false){
            nuevaSuscripcion.add(nuevoServicio);
            JOptionPane.showMessageDialog(null, "Guardado exitoso");
            gui.internal.FrmVer.lblTotal.setText(nuevaSuscripcion.toString());
        }
        else{
            JOptionPane.showMessageDialog(null, "Servicio ya existe. Por favor ingrese un nombre v√°lido");
        }
        
        reset();

        
    }//GEN-LAST:event_btnGuardarActionPerformed

