    private void reset(){
        txtNombreServicio.setText("");
        txtCostoMensual.setText("");
        cboCategoria.setSelectedIndex(0);
    }
    
    public boolean validarExistencia(Servicio nuevoServicio){
        boolean existe = false;
        for(Servicio servicio : nuevaSuscripcion.getAll()){
            if(servicio.getNombre().equals(nuevoServicio.getNombre())){
                existe = true;
            }
        }
        return existe;
    }

